import React, { useEffect } from "react";
import useStore from "../store/store";
import { useLocation, useNavigate } from "react-router-dom";
import Card from "../components/card";
import "../styles/AllPgs.css";

const AllPgs = () => {
  const { pgListings, fetchPgListings } = useStore();
  const navigate = useNavigate();
  const location = useLocation();

  const selectedCategory = location.state?.category || "Girls";

  useEffect(() => {
    fetchPgListings();
  }, [fetchPgListings]);

  const filteredPGs = pgListings.filter(
    (pg) => pg.category && pg.category.toLowerCase() === selectedCategory.toLowerCase()
  );

  return (
    <main className="allpgs-main">
      <div className="header-row">
    <button className="homepage-button" onClick={() => navigate("/")}>
      ‚Üê Home
    </button>
    <h3 className="category-title">
      All {selectedCategory} PGs
    </h3>
  </div>
      <section>
        <div className="card-container">
          {filteredPGs.length > 0 ? (
            filteredPGs.map((pg, index) => (
              <Card
                key={index}
                pg={pg}
                onClick={() => navigate(`/pg-details/${pg._id || index}`)}
              />
            ))
          ) : (
            <p className="no-pgs-text">
              No {selectedCategory.toLowerCase()} PGs available.
            </p>
          )}
        </div>
      </section>
    </main>
  );
};

export default AllPgs;
