import React from "react";
import Header from "./components/Header";
import BookCard from "./components/BookCard";
import Footer from "./components/Footer";
import "./styles.css";

const books = [
  {
    title: "The Handmaid's Tale",
    author: "Margaret Atwood",
    description: "A dystopian novel where women's bodies are controlled by the government.",
    image: "https://anylang.net/sites/default/files/styles/book_image/public/covers/handmaids-tale_0.jpg?itok=V263KypE",
    wikipedia: "https://en.wikipedia.org/wiki/The_Handmaid's_Tale",
    amazon: "https://www.amazon.in/Handmaids-Tale-Contemporary-Classics/dp/0099740915",
  },
  {
    title: "The Hunger Games",
    author: "Suzanne Collins",
    description: "A dystopian novel where children fight to the death in a televised event.",
    image: "https://m.media-amazon.com/images/I/71WSzS6zvCL._SY522_.jpg",
    wikipedia: "https://en.wikipedia.org/wiki/The_Hunger_Games",
    amazon: "https://www.amazon.com/Hunger-Games-Suzanne-Collins/dp/0439023483",
  },
];

const App = () => {
  return (
    <div>
      <Header />
      <main>
        <h2 className="genre">Girls</h2>
        <div className="card-container">
          {books.map((book, index) => (
            <BookCard key={index} book={book} />
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default App;
