import React from 'react'
import propTypes from 'prop-types'    
// importing 

export default function Header(props) {                             
            // default export     
            // props is js obiject that we are passing from parent compontent(app.js) to child
            // we need to pass props as an argument to access the title prop
            // props is an object that contains all the properties passed to the component

        //export default function Header({title}) {  
             //we can use curly braces to access title too but we use props as it helps to distinguish between other components

return (
    <nav class="navbar navbar-expand-lg bg-body-tertiary">
  <div class="container-fluid">

    <a class="navbar-brand" href="#">{props.title}</a>      
             {/* // passed in app.js */}
    
        {/*<a class="navbar-brand" href="#">{title}</a>    */}

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarSupportedContent">
      <ul class="navbar-nav me-auto mb-2 mb-lg-0">
        <li class="nav-item">
          <a class="nav-link active" aria-current="page" href="#">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" href="#">Link</a>
        </li>
      </ul>

        {props.searchBar ?   <form class="d-flex" role="search">
            <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/>
            <button class="btn btn-outline-success" type="submit">Search</button>
          </form>:""}
      
              {/* checks for the boolean var for displaying the search bar  */}

    </div>
  </div>
</nav>
)
}   

Header.propTypes = {
   title: propTypes.string,
            //strict type checking for robust code making it easier to catch buugs
   
}
