import React, { useEffect } from "react";
import useStore from "../store/store";
import { useLocation, useNavigate } from "react-router-dom";
import Card from "../components/card";
import "../styles/AllPgs.css";

const AllPgs = () => {
  const { pgListings, fetchPgListings } = useStore();
  const navigate = useNavigate();
  const location = useLocation();

  const selectedCategory = location.state?.category || "Girls";

  useEffect(() => {
    fetchPgListings();
  }, [fetchPgListings]);

  // Filter PGs by selected category
  const filteredPGs = pgListings.filter(
    (pg) => pg.category && pg.category.toLowerCase() === selectedCategory.toLowerCase()
  );

  return (
    <main>
      <section>
        <h3 className="category-title">
          All {selectedCategory} PGs
        </h3>

        {/* Go to Homepage Button */}
        <div style={{ textAlign: "center", marginBottom: "20px" }}>
          <button
            className="homepage-button"
            onClick={() => navigate("/")}
          >
            Go to Homepage
          </button>
        </div>

        <div className="card-container">
          {filteredPGs.length > 0 ? (
            filteredPGs.map((pg, index) => (
              <Card
                key={index}
                pg={pg}
                onClick={() => navigate(`/pg-details/${pg._id || index}`)}
              />
            ))
          ) : (
            <p>No {selectedCategory.toLowerCase()} PGs available.</p>
          )}
        </div>
      </section>
    </main>
  );
};

export default AllPgs;
