import React, { useState } from "react";
import "../styles/ListPg.css";

export default function PgApplyForm() {
  const [pgData, setPgData] = useState({
    pgName: "",
    address: "",
    landmark: "",
    gender: "",
    acType: "",
    roomOptions: [],
    rentAC: "",
    rentNonAC: "",
    extraCharges: "",
    extraDesc: "",
    meals: { Breakfast: false, Lunch: false, Dinner: false },
    description: "",
    contact: "",
    images: [],
  });

  const handleChange = (e) => {
    const { name, value, type, checked, files } = e.target;

    if (name.startsWith("meals.")) {
      const meal = name.split(".")[1];
      setPgData((prev) => ({
        ...prev,
        meals: { ...prev.meals, [meal]: checked },
      }));
    } else if (name === "images") {
      setPgData((prev) => ({ ...prev, images: Array.from(files) }));
    } else if (name === "roomOptions") {
      const option = value;
      setPgData((prev) => {
        const updated = prev.roomOptions.includes(option)
          ? prev.roomOptions.filter((r) => r !== option)
          : [...prev.roomOptions, option];
        return { ...prev, roomOptions: updated };
      });
    } else {
      setPgData((prev) => ({ ...prev, [name]: type === "checkbox" ? checked : value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const requiredFields = ["pgName", "address", "gender", "acType", "contact"];
    for (const field of requiredFields) {
      if (!pgData[field] || pgData[field].toString().trim() === "") {
        alert(`Please fill the required field: ${field}`);
        return;
      }
    }

    if (pgData.roomOptions.length === 0) {
      alert("Please select at least one room option");
      return;
    }

    if (pgData.extraCharges.trim() !== "" && pgData.extraDesc.trim() === "") {
      alert("Please describe the extra charges");
      return;
    }

    // Build FormData object for image upload
    const formData = new FormData();
    for (const key in pgData) {
      if (key === "images") {
        pgData.images.forEach((file) => formData.append("images", file));
      } else if (key === "meals" || key === "roomOptions") {
        formData.append(key, JSON.stringify(pgData[key]));
      } else {
        formData.append(key, pgData[key]);
      }
    }

    try {
      const response = await fetch("http://localhost:5000/api/pgs", {
        method: "POST",
        body: formData,
      });

      const result = await response.json();
      if (response.ok) {
        alert("PG submitted successfully!");
        console.log("Success:", result);
      } else {
        alert("Something went wrong: " + result.message);
      }
    } catch (err) {
      console.error("Error submitting:", err);
      alert("Server error occurred!");
    }
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="pg-form" encType="multipart/form-data">
        <h2 className="form-title">PG Submission Form</h2>

        <div className="form-group">
          <label>PG Name <span className="required">*</span></label>
          <input type="text" name="pgName" value={pgData.pgName} onChange={handleChange} required />
        </div>
        <div className="form-group">
        <label>Owner Name <span className="required">*</span></label>
        <input type="text" name="ownerName" value={pgData.ownerName || ""} onChange={handleChange} required  />
        </div>

        <div className="form-group">
          <label>Address <span className="required">*</span></label>
          <textarea name="address" value={pgData.address} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Nearby Landmark</label>
          <input type="text" name="landmark" value={pgData.landmark} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Specified Gender <span className="required">*</span></label>
          <select name="gender" value={pgData.gender} onChange={handleChange} required>
            <option value="">--Select--</option>
            <option value="boys">Boys</option>
            <option value="girls">Girls</option>
            <option value="both">Both</option>
          </select>
        </div>

        <div className="form-group">
          <label>AC Type <span className="required">*</span></label>
          <select name="acType" value={pgData.acType} onChange={handleChange} required>
            <option value="">--Select--</option>
            <option value="ac">AC</option>
            <option value="non-ac">Non AC</option>
            <option value="both">Both</option>
          </select>
        </div>

        <div className="form-group">
          <label>Room Options <span className="required">*</span></label>
          <div className="checkbox-group">
            {["1", "2", "3", "4", "more"].map((num) => (
              <label key={num}>
                <input
                  type="checkbox"
                  name="roomOptions"
                  value={num}
                  checked={pgData.roomOptions.includes(num)}
                  onChange={handleChange}
                />
                {num}
              </label>
            ))}
          </div>
        </div>

        <div className="form-row">
          <div className="form-group half-width">
            <label>Rent per Bed (AC)</label>
            <input type="number" name="rentAC" value={pgData.rentAC} onChange={handleChange} />
          </div>
          <div className="form-group half-width">
            <label>Rent per Bed (Non AC)</label>
            <input type="number" name="rentNonAC" value={pgData.rentNonAC} onChange={handleChange} />
          </div>
        </div>

        <div className="form-group">
          <label>Extra Charges</label>
          <input type="text" name="extraCharges" value={pgData.extraCharges} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Extra Charges Description</label>
          <textarea
            name="extraDesc"
            value={pgData.extraDesc}
            onChange={handleChange}
            disabled={!pgData.extraCharges.trim()}
            required={pgData.extraCharges.trim()}
          />
        </div>

        <div className="form-group">
          <label>Meals Provided</label>
          <div className="checkbox-group">
            {["breakfast", "lunch", "dinner"].map((meal) => (
              <label key={meal}>
                <input
                  type="checkbox"
                  name={`meals.${meal}`}
                  checked={pgData.meals[meal]}
                  onChange={handleChange}
                />
                {meal}
              </label>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label>Description</label>
          <textarea name="description" value={pgData.description} onChange={handleChange} />
        </div>

        <div className="form-group">
          <label>Contact Number <span className="required">*</span></label>
          <input type="tel" name="contact" value={pgData.contact} onChange={handleChange} required />
        </div>

        <div className="form-group">
          <label>Upload Images</label>
          <input type="file" name="images" accept="image/*" multiple onChange={handleChange} />
        </div>

        <button type="submit" className="submit-btn">Submit PG</button>
      </form>
    </div>
  );
}
