import './App.css';
import React, { useState } from 'react';    
import {Header} from "./Components/Header.js";
import {Todos} from "./Components/Todos.js";   
import {Footer} from "./Components/Footer.js";




function App() {
  const onDelete = (todo) => {
    console.log("I am onDelete of todo", todo);
    setTodos(todos.filter((e)=>{      // useState fn to update state 
         return e !== todo;         // return after deleting
    }))
    
  }
  let [todos,setTodos] = useState( [       //it is like a hook, helps to initaalize the state os var and if state changes then component re-render the new val
   //todos - list of tasks       setTodo -fn
    {
      sno: 1,
      title: "Task1",
      desc: "1st"
    },
    {
      sno: 2,
      title: "Task2",
      desc: "2nd"
    },
    {
      sno: 3,
      title: "Call ABC",
      desc: "3rd"
    },
    {
      sno:4,
      title: "Task4",
      desc: "4th"
    },
     {
      sno:5,
      title: "Task5",
      desc: "5th"
    },
     {
      sno:6,
      title: "Task6",
      desc: "6th"
    }
  ]);

  return (
    <>
      <Header title="My Todos List" searchBar={true}/>   
   {/* creating header component */}
      {/* //sends title to be added to header  */}
      <Todos todos={todos} onDelete={onDelete}/>
      <Footer/>
    </>
  );
}
export default App;
