import './App.css';
import React, { useState } from 'react';    
import Header from "./Components/Header.js";    //Default exports can be accessed by name 
import {Todos} from "./Components/Todos.js";   
import {Footer} from "./Components/Footer.js";
// named import



function App() {
 
  //child sends 
  const onDelete = (todo) => {
    console.log("I am onDelete of todo", todo);
    
    let index=  todos.indexOf()
    setTodos(todos.filter((e)=>{      // useState fn to update state 
         return e !== todo;         // return after deleting
    }))
    
  }

  //making todos as a js object
  let [todos,setTodos] = useState( [       //it is like a hook, helps to initaalize the state os var and if state changes then component re-render the new val
   //todos - list of tasks       setTodo -fn
    {
      sno: 1,
      title: "Task1",
      desc: "1st"
    },
    {
      sno: 2,
      title: "Task2",
      desc: "2nd"
    },
    {
      sno: 3,
      title: "Call ABC",
      desc: "3rd"
    },
    {
      sno:4,
      title: "Task4",
      desc: "4th"
    },
     {
      sno:5,
      title: "Task5",
      desc: "5th"
    },
     {
      sno:6,
      title: "Task6",
      desc: "6th"
    }
  ]);

  return (
    <>
    <Header title={"My Todos List"} searchBar={true}/>   
      
        {/* <Header title={45} searchBar={false}/>    
              It will be displayed be will show error due to proptypes  
        */}
        {/* <Header searchBar={false}/>    
              It will show the default prop  
        */}


          {/* creating header component which can be reused */}
          {/* sends title to be added to header  -> sending data from parent to child compnent*/}
     
    <Todos todos={todos} onDelete={onDelete}/>
          {/* further has todoItem component to render individual todo items */}
          {/* app.js the parent that provide todos.js with two properties */}
     
     
    <Footer/>

    </>
  );
}
export default App;
