import { useState } from "react";

const dummyData = [
  {
    name: "A",
    image: "/assests/images/pg1.png",
    location: "A",
    category: "Girls"
  },
  {
    name: "Sunrise PG",
    image: "/assests/images/pg2.png",
    location: "Delhi, India",
    description: "Affordable and student-friendly PG in prime location.",
    category: "Boys"
  },
  {
    name: "Cozy Coed PG",
    image: "/assests/images/pg3.png",
    location: "Gurgaon, Haryana",
    description: "Coed PG with all facilities and great connectivity.",
    category: "Coed"
  }
];

const useStore = () => {
  const [pgListings, setPgListings] = useState(dummyData);

  const fetchPgListings = () => {
    setPgListings(dummyData);
  };

  return { pgListings, fetchPgListings };
};

export default useStore;
