import React, { useEffect } from "react";
import useStore from "../store/store";
import { useNavigate } from "react-router-dom";
import Card from "../components/card";
import "../styles/PgListing.css";

const PgListings = () => {
  const { pgListings, fetchPgListings } = useStore();
  const navigate = useNavigate();

  useEffect(() => {
    fetchPgListings();
    // console.log("PG Listings:", pgListings);
  }, []);

  const girlsPG = pgListings.filter(pg => pg.category === "Girls");
  const boysPG = pgListings.filter(pg => pg.category === "Boys");
  const coedPG = pgListings.filter(pg => pg.category === "Coed");

  return (
    <main>
      <h2 className="genre">Available PGs</h2>
      <div class="add-pg-container">
        <button className="add-pg-btn" onClick={() => navigate("/add-pg")}>
        Add New PG  </button></div>
      <hr />
      {[
        { title: "Girls PG", listings: girlsPG },
        { title: "Boys PG", listings: boysPG },
        { title: "Coed PG", listings: coedPG }
      ].map(({ title, listings }, sectionIndex) => (
        <section key={sectionIndex}>
          <h3 className="category-title">{title}</h3>
          <div className="card-container">
            {listings.map((pg, index) => (
              <Card key={index} pg={pg} onClick={() => navigate(`/pg-details/${index}`)} />
            ))}
          </div>
        </section>
      ))}
    </main>
  );
};

export default PgListings;
